const initialState = {data:{}}

const singleUserReducer = (state=initialState.data, action) => {

    switch (action.type) {
        case 'GET_USER': {
            return {...state, data:[action.payload]}
        }
        default: {
            return state
        }
    }
}

export default singleUserReducer
