const initialState = {data:{}}

const usersReducer = (state=initialState, action) => {

    switch (action.type) {
        case 'GET_ALL_USERS': {
            return {...state, data:[...state.data, action.payload]}
        }
        default: {
            return state
        }
    }
}

export default usersReducer
