const initialState = {users:{}, user:{}}

const usersReducer = (state=initialState, action) => {

    switch (action.type) {
        case 'GET_ALL_USERS': {
            return {...state, users:[...state.users, action.payload]}
        }
        case 'GET_USER': {
            return {...state, user:action.payload}
        }
        default: {
            return state
        }
    }
}

export default usersReducer
